INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (20,'Palacio Episcopal','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Palacio Episcopal" width="190" height="143" src="upload/userfiles/image/2-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Este elegante palacio se encuentra en un lateral de la Plaza de Santa María que mandó reedificar el obispo cauriense Pedro García de Galarza a finales del siglo XVI, y cuyo escudo se puede apreciar sobre la ventana central de la segunda planta. Destaca su elegante portada de arco de medio punto con doble almohadillado de sillería, flanqueada por dos medallones que representan a un indio y una india americanos. Este palacio consta de dos portadas más, la procedente del antiguo seminario de San Pedro, fundado en siglo XVII por el mismo obispo Galarza y que se trasladó hasta aquí tras su derribo; y de la calle del Arco de la Estrella. La situación de esta hermosa construcción, cuyas traseras daban al adarve y a la Puerta Nueva, fue motivo de los pleitos tenidos con el Marqués de la Quinta de la Enjarada por abrir y remodelar el Arco de la Estrella, para dar paso a sus carruajes. En la actualidad es residencia del Obispo de la Diócesis de Coria-Cáceres.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Situada en Plaza de Santa María</p></span></td></tr></tbody></table><p>&nbsp;</p>',20, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (21,'Episcopal Palace','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Episcopal Palace" width="190" height="143" src="upload/userfiles/image/2-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">This elegant palace is on one side of the Plaza of Santa María. It was rebuilt on the orders of the Bishop Pedro García de Galarza, of Coria, at the end of the 16th century, whose shield can be seen over the central window of the second floor. It has an elegant facade with semi-circular arches with a double masonry bolster, flanked by two medallions that represent a male and female American Indian. This palace has two more facades, one from the former Seminary of San Pedro, founded in the 17th century by the same Bishop Galarza and moved here after its demolition; and the other on the street Arco de la Estrella. The location of this beautiful building, the back of which faces onto the city walls and the Puerta Nueva (New Gate), was the cause of legal disputes with the Marquis de la Quinta de la Enjarada to open and remodel the Arch de la Estrella, so his carriages could pass through. Nowadays it is the residence of the Bishop of the Diocese of Coria-Caceres.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Located in Plaza de Santa María.</p></span></td></tr></tbody></table><p>&nbsp;</p>',20, 3);
