INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (40,'Palacio de Hernando de Ovando','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Palacio de Hernando de Ovando" width="190" height="143" src="upload/userfiles/image/4-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Levantado en el siglo XVI, muy cercano a la iglesia de Santa María. En la fachada posee un escudo esgrafiado del XVIII, en el que aparecen representados los linajes familiares que lo habitaron. El edificio se compone de cuatro crujías en torno a un patio claustrado. Sobre la puerta de entrada al primer piso de la torre hay una inscripción que se refiere al año 1480. Desde ese punto el acceso a la parte superior se realiza a través de varios tramos de escalera adosados a los muros.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Situado en Plaza de Santa María.</p></span></td></tr></tbody></table><p>&nbsp;</p>',40, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (41,'Palace of Hernando de Ovando','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Palace of Hernando de Ovando" width="190" height="143" src="upload/userfiles/image/4-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Built in the 16th century, very close to the Church of Santa María. On the facade there is a sgraffito shield from the 18th century, in which the family lineages of those who lived here are represented. The building is made up of four areas around a cloistered patio. Over the door to the first floor of the tower there is an inscription that refers to the year 1480. From here access to the upper sections is via several stairways that are up against the walls.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Located in Plaza de Santa María.</p></span></td></tr></tbody></table><p>&nbsp;</p>',40, 3);
