INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (50,'Casa y Torre Carbajal','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Casa y Torre Carbajal" width="190" height="143" src="upload/userfiles/image/5-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Su fachada sigue la tipología típica de los palacios cacereños con balcón de esquina, escudo frontal de grandes dimensiones y puerta en arco de medio punto con dovelas radiales. Su huerto - del que destaca la higuera - es un claro ejemplo de los espacios verdes interiores que pueden encontrarse aún hoy en estas edificaciones de la Zona Monumental.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">En este edificio se encuentran las dependencias del Patronato de Turismo y Artesanía de la Diputación y el Centro de Interpretación de las Comarcas de la Provincia de Cáceres.</p></span></td></tr></tbody></table><p>&nbsp;</p>',50, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (51,'House and Tower Carbajal','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="House and Tower Carbajal" width="190" height="143" src="upload/userfiles/image/5-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Its facade is in keeping with the typical typology of the palaces in Caceres; with a corner balcony, a large shield at the front and a door in a semi-circular arch with radial voussoirs.Its orchard, with an outstanding fig tree, is a clear example of the inner green areas that you can still find today in these buildings within the Monumental Area.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">The offices of the Board of Tourism and Handicrafts of the Provincial Council and the Centre of Interpretation of the Regions of the Province of Caceres are in this building.</p></span></td></tr></tbody></table><p>&nbsp;</p>',50, 3);
