INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (60,'Iglesia Concatedral de Santa María','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Iglesia Concatedral de Santa María" width="190" height="143" src="upload/userfiles/image/6-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">En la esquina, la escultura de San Pedro de Alcántara, del extremeño Enrique Pérez Comendador, tiene fama de atraer la suerte si le tocan los dedos de los pies, de ahí su brillo. Es la Iglesia principal de la ciudad. Fue construida entre los siglos XV al XVI sobre una obra anterior. Las portadas siguen modelos góticos y en la del Evangelio destaca un pilar con ménsula sosteniendo la Virgen. La torre es de tres cuerpos, el central tiene un medallón con jarrón de azucenas, símbolo de la Vírgen. El interior es de planta rectangular de tres naves, con arcos apuntados y bóvedas de crucería. Destaca su retablo del siglo XVI de cedro y pino sin policromar dedicado a la Asunción de la Virgen, realizado por Roque Balduque y Guillen Ferrant. Célebres son las capillas de los Carvajal, con un retablo barroco con San Miguel Arcángel del XVII, y la del Cristo Negro, imagen del siglo XV.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">En un banco esculpido en la portada principal se reunía antiguamente el Concejo Municipal.</p></span></td></tr></tbody></table><p>&nbsp;</p>',60, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (61,'Church Co-cathedral of Santa María','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Church Co-cathedral of Santa María" width="190" height="143" src="upload/userfiles/image/6-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">In the corner, the sculpture of San Pedro de Alcantara, by Enrique Pérez Comendador of Extremadura, is reputed to bring you luck if you touch its toes, which is why they are shiny.This is the city’s main church. It was built between the 15th and 16th centuries over an earlier building. It has Gothic facades and on the Gospel side there is a pillar with a corbel supporting the Virgin. The tower has three sections; the central one has a medallion with a vase of Madonna lilies, the symbol of the Virgin.Inside it is a rectangular building with three naves, with pointed arches and ribbed vaults. The altarpiece, from the 16th century, is made of cedar and pine, without polychrome, and is dedicated to the Assumption of the Virgin. It was made by Roque Balduque and Guillen Ferrant. The Chapels de los Carvajal are famous, with a Baroque altarpiece with Saint Michael the Archangel from the 17th century and Cristo Negro, an image from the 15th century.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">The Council meets on a carved bench outside the church after the Main Mass.</p></span></td></tr></tbody></table><p>&nbsp;</p>',60, 3);
