INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (70,'Casa de los Moragas','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Casa de los Moragas" width="190" height="143" src="upload/userfiles/image/7-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Se trata de una casa del siglo XV adquirida por la Diputación Provincial de Cáceres que tras una importante rehablitación se convierte en el Centro Provincial de Artesania.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">La Casa Moraga expone una muestra representativa de la artesanía extremeña: alfarería, cestería, cobre, corcho, forja, madera, tejidos, orivería, etc.</p></span></td></tr></tbody></table><p>&nbsp;</p>',70, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (71,'House of Moragas','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="House of Moragas" width="190" height="143" src="upload/userfiles/image/7-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">It is a XV century house acquired by the Provincial Council of Cáceres after major rehablitación becomes the Provincial Center for Crafts.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Moraga House presents a representative sample of the craft Extremadura: pottery, basketry, copper, cork, forging, wood, fabric, etc.</p></span></td></tr></tbody></table><p>&nbsp;</p>',70, 3);
