INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (80,'Casa de los Golfín y Santa Marta','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Casa de los Golfín y Santa Marta" width="190" height="143" src="upload/userfiles/image/8-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Este edificio fue reconstruido con elementos renacentistas del siglo XVI. En la fachada podemos apreciar su portada, en arco de medio punto con grandes dovelas, y por encima de ésta, una ventana flanqueada por dos escudos, uno de los Golfines y el otro de Portocarrero. En su interior puede verse un pequeño patio que, a pesar de tener un trazado irregular y contar sólo con dos lados porticados, resulta de gran armonía por el suave tratamiento de sus columnas y capiteles.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">En la actualidad se han instalado en esta casa algunos servicios técnicos de la Diputación Provincial de Cáceres.</p></span></td></tr></tbody></table><p>&nbsp;</p>',80, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (81,'House of Golfín and Santa Marta','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Casa of Golfín and Santa Marta" width="190" height="143" src="upload/userfiles/image/8-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">This building was rebuilt in the sixteenth century Renaissance elements. In the front we can see its cover, in a semicircular arch with large voussoirs, and above it, a window flanked by two shields, one of the Olive Ridley and the other Portocarrero. Inside you can see a small courtyard, despite having an irregular shape and having only two arcaded sides, is of great harmony for the smooth processing of your columns and capitals.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Some technical services of the  Diputación Provincial de Cáceres are currently installed in this house.</p></span></td></tr></tbody></table><p>&nbsp;</p>',80, 3);
