INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (90,'Palacio de la Diputación Provincial de Cáceres','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Palacio de la Diputación Provincial de Cáceres" width="190" height="143" src="upload/userfiles/image/9-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Es un edificio de estilo renacentista, construido en el siglo XVI. Está asentado sobre el lugar que ocuparon el Palacio de Hernán Pérez de Ulloa y el Convento de Santa María de Jesús, fundado en el siglo XV por las monjas jerónimas. Su fachada procede del Colegio de San Pedro, Seminario del Obispo Galarza. La portada es adintelada, de frontón triangular, coronando las columnas laterales adosadas con escudos de Galarza. En el frontón hay una imagen de la Virgen irradiante, en el interior de un óvalo. En la esquina de la derecha hay otro escudo de Galarza.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Actualmente es la sede de la Diputación Provincial.</p></span></td></tr></tbody></table><p>&nbsp;</p>',90, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (91,'Palace of Diputación Provincial de Cáceres','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Palace of Diputación Provincial de Cáceres" width="190" height="143" src="upload/userfiles/image/9-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">It is a Renaissance-style building, built in the sixteenth century. Sits on the place occupied the Palace of Hernan Perez de Ulloa and the Convent of Santa Maria de Jesus, founded in the fifteenth century by the nuns Hieronymite. Its facade is from the Colegio de San Pedro, Seminar Obispo Galarza. The cover is a lintel, a triangular pediment, columns crowned with attached side shields Galarza. On the pediment there is a radiant image of the Virgin, inside an oval. In the right corner there is another shield Galarza.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify"> It now houses the County Council.</p></span></td></tr></tbody></table><p>&nbsp;</p>',90, 3);
