INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (100,'Palacio de los Golfines de Abajo','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Casa de los Golfines de Abajo" width="190" height="143" src="upload/userfiles/image/10-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">En este palacio se alojaron los Reyes Católicos, de ahí el escudo en la fachada plateresca y la alusión "Fer de Fer", Fernando (Golfín) de Fernando (el Católico)El tamaño de este palacio es considerable, ya que ocupó el espacio de casi una veintena de casas. Aún así, posee una de las fachadas más decorativas y vistosas del Conjunto Monumental. Alfíz, ventanas geminadas, escudos, cartelas, medallones al gusto clásico y un remate de grifos afrontados, similar al del Palacio de Monterrey de Salamanca, han hecho de este edificio uno de los más significativos y reproducido en múltiples soportes (pintura, fotografía, películas...)</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Situado en Plaza de los Golfines</p></span></td></tr></tbody></table><p>&nbsp;</p>',100, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (101,'Palace of Golfines de Abajo','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Palace Golfines de Abajo" width="190" height="143" src="upload/userfiles/image/10-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">The Catholic Kings stayed in this palace, hence the shield on the plateresque facade and the reference ‘Fer de Fer’, Fernando (Golfín) de Fernando (the Catholic).This palace is of a considerable size, occupying the space of nearly twenty houses. It has one of the most decorative, bright and colourful facades of the Monumental Ensemble. Alfiz, double windows, shields, cartouches and classical style medallions, all crowned by opposing griffins, similar to that of the Palace of Monterrey in Salamanca, have made this one of the most important buildings and most reproduced in multiple mediums (painting, photography, films, etc.).</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Situated in Plaza de los Golfines</p></span></td></tr></tbody></table><p>&nbsp;</p>',100, 3);
