INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (110,'Casa de los Becerra','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Casa de los Becerra" width="190" height="143" src="upload/userfiles/image/11-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">El noble que la mandó construir, Luis de Becerra, la disfrutó poco, pues pasó casi toda su vida batallando por distintas causas. Curiosamente se dice que murió de una enfermedad en la cama. Imponente casa de finales del siglo XV y principios del XVI. Concentra los elementos propios de las fachadas de las mansiones cacereñas (puerta principal en arco de medio punto realizado con largas dovelas, alfíz, escudo) en muy poco espacio, no pareciendo lo amplia que realmente es.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">En ella está instalada la fundación Mercedes Calle y Carlos Ballesteros. Se puede visitar el interior.</p></span></td></tr></tbody></table><p>&nbsp;</p>',110, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (111,'House de los Becerra','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="House de los Becerra" width="190" height="143" src="upload/userfiles/image/11-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">The nobleman that ordered it to be built, Luis de Becerra, didn’t get to enjoy it much as he spent nearly his whole life fighting for various causes. Strangely, it is said that he died in bed from an illness.Imposing house dating from the end of the 15th and the beginning of the 16th century. The typical elements of the facades of mansions in Caceres (the main door in a semi-circular arch with large voussoirs, alfiz and shield) are concentrated in a very small space, so it doesn’t seem as big as it actually is.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">The Mercedes Calle and Carlos Ballesteros Foundation is installed inside, which can be visited.</p></span></td></tr></tbody></table><p>&nbsp;</p>',110, 3);
