INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (120,'Colegio de la Compañía de Jesús','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Colegio de la Compañía de Jesús" width="190" height="143" src="upload/userfiles/image/12-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Antiguo Seminario Jesuita y Colegio Mayor "Luisa de Carvajal", en la actualidad acoge servicios territoriales de la Junta de Extremadura, la Filmoteca y el Centro de Exposiciones San Jorge y alberga anualmente la Feria de Arte Contemporáneo "Foro Sur"</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Plaza de San Jorge</p></span></td></tr></tbody></table><p>&nbsp;</p>',120, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (121,'College de la Compañía de Jesús','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="College de la Compañía de Jesús" width="190" height="143" src="upload/userfiles/image/12-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">The former Jesuit Seminary and Main College Luisa de Carvajal now houses local services of the Regional Government of Extremadura, the film library and the San Jorge Exhibition Centre and the annual Contemporary Art Fair, Foro, is held here.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Plaza de San Jorge</p></span></td></tr></tbody></table><p>&nbsp;</p>',120, 3);
