INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (130,'Iglesia de San Francisco Javier','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Iglesia de San Francisco Javier" width="190" height="143" src="upload/userfiles/image/13-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Este conjunto de Iglesia y Colegio construido por la Compañía de Jesús en el siglo XVIII, siguiendo el estilo barroco, preside un gran espacio en el centro de la Zona Monumental. La fachada de la iglesia tiene arco de medio punto y una hornacina cobijando la imagen de San Francisco Javier y está flanqueada por dos torres cuadradas rematadas piramidalmente. El interior es de una sola nave, con capillas laterales y cúpula sobre el crucero. El retablo mayor tiene columnas corintias enmarcando un lienzo que representa el "Milagro del Cangrejo".</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Está situada en la Plaza de San Jorge, entre las plazas más antiguas de Santa María y de San Mateo. La escultura de San Jorge, patrón de la ciudad, le da nombre y preside la plaza desde una hornacina.</p></span></td></tr></tbody></table><p>&nbsp;</p>',130, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (131,'Church of San Francisco Javier','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Church of San Francisco Javier" width="190" height="143" src="upload/userfiles/image/13-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">This joint church and college was built by the Company of Jesus in the 18th century. It is Baroque style and occupies a large area in the centre of the Monumental Area.The church’s facade has a semi-circular arch and a niche that shelters the image of San Francisco Javier and is flanked by two square towers that finish in pyramids.Inside is a single nave, with side chapels and a cupola over a stone cross. The main altarpiece has Corinthian columns framing a canvas that represents the ‘Miracle of the Crab’.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">This is in the Plaza de San Jorge, between the oldest squares of Santa María and San Mateo. The square takes its name from the sculpture of Saint George, the city’s Patron Saint, which presides over the square from a niche.</p></span></td></tr></tbody></table><p>&nbsp;</p>',130, 3);
