INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (140,'Casa del Águila','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Casa del Águila" width="190" height="143" src="upload/userfiles/image/14-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Casa señorial que perteneció a la familia de los Sande, como así indica el escudo con el águila. En la fachada destaca una preciosa ventana gótica del siglo XV. Como muchos de estos palacetes, sus reformas de acondicionamiento se han sucedido en el tiempo.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Callejón de la Monja</p></span></td></tr></tbody></table><p>&nbsp;</p>',140, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (141,'House del Águila','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="House del Águila" width="190" height="143" src="upload/userfiles/image/14-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Stately home that belonged to the Sande family, as indicated by the shield with the eagle. In the facade there is a beautiful Gothic window from the 15th century.Like many of these small palaces it has undergone reformations over time.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Callejón de la Monja</p></span></td></tr></tbody></table><p>&nbsp;</p>',140, 3);
