INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (160,'Torre de Sande','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Torre de Sande" width="190" height="143" src="upload/userfiles/image/16-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Impresionante torre construida en el siglo XIV, tratándose de una de las más antiguas de la ciudad. Presenta un soberbio matacán defensivo, además de una ventana geminada y otros elementos de estilo Gótico. Perteneció a una casa señorial hoy desaparecida. De hecho, lo único que queda en pie de la misma es esta torre.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Calle de Orellana</p></span></td></tr></tbody></table><p>&nbsp;</p>',160, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (161,'Tower of Sande','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Tower of Sande" width="190" height="143" src="upload/userfiles/image/16-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">This impressive tower, built in the 14th century, is one of the oldest in the city.It has a magnificent defensive machicolation, as well as a double window and other Gothic elements.It belonged to a stately home that has disappeared. In fact, this tower is the only thing that remains standing from it.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Calle de Orellana</p></span></td></tr></tbody></table><p>&nbsp;</p>',160, 3);
