INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (180,'Palacio de las Cigüeñas','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Palacio de las Cigüeñas" width="190" height="143" src="upload/userfiles/image/18-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Es un edificio equilibrado, del que destaca su torre, mucho más elevada que las del resto de los palacios del conjunto monumental, debido a que cuando la Reina Isabel la Católica mandó desmochar las torres cacereñas, hizo una excepción con este que entonces se estaba construyendo por orden del capitán Diego de Ovando, vasallo y miembro de su Real Consejo.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">Actual sede de la Comandancia Militar de Cáceres, en su interior se encuentra una colección de armas, conocida como el Museo de Armas. Se suelen organizar exposiciones con cierta asiduidad.</p></span></td></tr></tbody></table><p>&nbsp;</p>',180, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (181,'Palace de las Cigüeñas','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Palace de las Cigüeñas" width="190" height="143" src="upload/userfiles/image/18-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">This is a well-balanced building, in which the tower stands out, being much higher than those of the rest of the palaces in the Monumental Ensemble, as when the Queen Isabel the Catholic ordered all the towers of Caceres to be lopped off she made an exception of this one, which was at that time being built on the orders of Captain Diego de Ovando, vassal and member of her Royal Council.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">This is now the Military Command Headquarters of Caceres, with a collection of weapons, known as the Weapons Museum, inside. They usually organize exhibitions fairly regularly.</p></span></td></tr></tbody></table><p>&nbsp;</p>',180, 3);
