INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (190,'Casa de las Veletas','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Casa de las Veletas" width="190" height="143" src="upload/userfiles/image/19-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Este solar que ocupan ahora el Palacio de las Veletas y la Casa de los Caballos estaba destinado anteriormente a Alcazaba árabe. Prueba de ello es su aljibe de los siglos XI-XII. El resto de lo que actualmente puede verse del edificio es obra de los siglos XV, XVII y XVIII.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">El Museo de Cáceres contiene una colección que abarca desde la Prehistoria hasta la época Visigoda, otra de Etnografía, además del aljibe hispano-árabe y la colección de Bellas Artes con obras del Museo del Prado (Lucas Jordán, Vicente Carducho, Antonio Mª Esquivel...), del Obispado de Coria-Cáceres (tallas, pinturas y obras de orfebrería religiosa) y de la Diputación Provincial (obras de artistas contemporáneos como Alberto Sánchez, Canogar, Chirino, Genovés, Gordillo, Millares, Oteiza, Palazuelo, Saura, Vostell, Zóbel... ). Además cuenta con una sala de exposiciones temporales.</p></span></td></tr></tbody></table><p>&nbsp;</p>',190, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (191,'House de las Veletas','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="House de las Veletas" width="190" height="143" src="upload/userfiles/image/19-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">This spot, which is now occupied by the Palace de las Veletas and the House de los Caballos, was previously the site of an Arabic Alcazaba (citadel). The cistern from the 11th to 12th centuries is proof of this. The rest of what you can now see of the building dates from the 15th, 17th and 18th centuries.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">The Museum of Caceres has one collection that spans from prehistory to the Visigoth era and another of ethnography, as well as the Hispanic-Arabic cistern and the collection of Fine Arts with works from the Prado Museum (Lucas Jordán, Vicente Carducho, Antonio Mª Esquivel, etc.), the Bishopric of Coria-Caceres (sculptures, paintings and religious articles of gold and silver) and the Provincial Council (works by contemporary artists such as Alberto Sánchez, Canogar, Chirino, Genovés, Gordillo, Millares, Oteiza, Palazuelo, Saura, Vostell, Zóbel, etc.). It also has a temporary exhibition room.</p></span></td></tr></tbody></table><p>&nbsp;</p>',190, 3);
