INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (200,'Convento de San Pablo','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Convento de San Pablo" width="190" height="143" src="upload/userfiles/image/20-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Beaterio fundado en el siglo XV, más tarde fue erigido convento. Sigue modelos góticos en la portada de la iglesia. En el convento residen aún monjas clarisas. Otros conventos habitados son el Convento de Santa Clara, en la plaza del mismo nombre, y el Convento de las Jerónimas, en la antigua Enfermería de San Antonio, en la calle Olmo.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">En este convento de monjas de clausura se pueden comprar dulces artesanales de gran fama.</p></span></td></tr></tbody></table><p>&nbsp;</p>',200, 2);
INSERT INTO descripciones(oid, descripcion, informacion, objeto_oid, idioma_oid) VALUES (201,'Convent of San Pablo','<table border="0" cellspacing="0" cellpadding="0" width="300" align="center" style="clear: both"><tbody><tr><td><div style="text-align: center"><img alt="Convent of San Pablo" width="190" height="143" src="upload/userfiles/image/20-0.jpg" /></div></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><strong>Descripci&oacute;n:</strong></td></tr><tr><td bgcolor="#ffffff"><p style="text-align: justify">Beaterio (convent for lay sisters) founded in the 15th century, with the actual convent being built later. The church’s facade is in the Gothic style. Nuns of the Order of Saint Clare live in the convent. Other inhabited convents are that of Santa Clara, in the square of the same name, and the Convent de las Jerónimas, in the old Infirmary of San Antonio, in Olmo Street.</p></td></tr></tbody></table><table border="1" cellspacing="0" cellpadding="0" width="300" align="center"><tbody><tr><td bgcolor="#6699ff" valign="top" colspan="2"><span class="texto_fichas"><strong>Observaciones:</strong></span></td></tr><tr><td bgcolor="#ffffff" valign="top"><span class="texto"><p style="text-align: justify">In this convent of cloistered nuns you can buy hand-made sweets of huge renown.</p></span></td></tr></tbody></table><p>&nbsp;</p>',200, 3);
